mapper.update:
update member
set pwd = #{pwd}
where user_id=#{userid}

mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from(
        select USER_NO, user_id, user_name, email, hp, regdate, outdate, user_status
	    from member

        	where ${searchCondition} like '%' 
        			|| #{searchKeyword} || '%'

        order by regdate
    )A    
)

where RNUM>#{firstRecordIndex}
  and RNUM<=#{firstRecordIndex }+ #{recordCountPerPage}

mapper.update:
update member
set outdate=sysdate 
where user_no=#{userNo}

mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from(
	    select * from member


				user_no=#{userNo} 

			and regdate >= #{startDay}

			and regdate < to_date(#{endDay})+1	

        order by regdate desc
    )A    
)

where RNUM>#{firstRecordIndex}
  and RNUM<=#{firstRecordIndex }+ #{recordCountPerPage}
