mapper.select:
select * 
from
(
	select rownum as RNUM, A.*
	from(
		select qna_no, user_id, title, category_no, content, fileName, fileSize, originFileName, status, regDate, no
		from qnarere

			where ${searchCondition} like '%'
					|| #{searchKeyword} || '%'

		order by qna_No desc
	)A
)

where RNUM>#{firstRecordIndex}
  and RNUM<=#{firstRecordIndex }+ #{recordCountPerPage}

mapper.update:
update qna
set title = #{title}, category_no=#{categoryNO}, content=#{content}, status=#{status}
where qna_no=#{qnaNo}
